import React, { useState, useEffect } from 'react';
import { useNavigate, useSearchParams, Link } from 'react-router-dom';
import axios from 'axios';
import toast from 'react-hot-toast';

const API = process.env.REACT_APP_API_URL || 'http://localhost:5000/api';

export default function AddProduct() {
  const navigate = useNavigate();
  const [searchParams] = useSearchParams();
  const editId = searchParams.get('edit');
  const [loading, setLoading] = useState(false);
  const [images, setImages] = useState([]);

  const [form, setForm] = useState({
    name: '', description: '', price: '', originalPrice: '', category: 'set-combo',
    stock: '', material: '', sizes: '', colors: '', isFeatured: false, isAvailable: true
  });

  // ‡¶Ü‡¶™‡¶®‡¶æ‡¶∞ ‡¶õ‡¶¨‡¶ø ‡¶Ö‡¶®‡ßÅ‡¶Ø‡¶æ‡ßü‡ßÄ ‡¶∂‡ßÅ‡¶ß‡ßÅ‡¶Æ‡¶æ‡¶§‡ßç‡¶∞ ‡¶è‡¶á ‡¶∏‡¶æ‡¶á‡¶ú‡¶ó‡ßÅ‡¶≤‡ßã ‡¶•‡¶æ‡¶ï‡¶¨‡ßá
  const availableSizes = ['2.2"', '2.4"', '2.6"', '2.8"'];

  useEffect(() => {
    if (editId) {
      axios.get(`${API}/products/${editId}`).then(r => {
        const p = r.data;
        setForm({
          ...p,
          sizes: Array.isArray(p.sizes) ? p.sizes.join(', ') : (p.sizes || ''),
          colors: Array.isArray(p.colors) ? p.colors.join(', ') : (p.colors || '')
        });
      });
    }
  }, [editId]);

  const toggleSize = (s) => {
    // ‡¶¨‡¶∞‡ßç‡¶§‡¶Æ‡¶æ‡¶® ‡¶∏‡¶ø‡¶≤‡ßá‡¶ï‡ßç‡¶ü ‡¶ï‡¶∞‡¶æ ‡¶∏‡¶æ‡¶á‡¶ú‡¶ó‡ßÅ‡¶≤‡ßã‡¶ï‡ßá ‡¶Ö‡ßç‡¶Ø‡¶æ‡¶∞‡ßá‡¶§‡ßá ‡¶∞‡ßÇ‡¶™‡¶æ‡¶®‡ßç‡¶§‡¶∞
    let current = form.sizes ? form.sizes.split(',').map(x => x.trim()).filter(Boolean) : [];

    const idx = current.indexOf(s);
    if (idx > -1) {
      current.splice(idx, 1); // ‡¶•‡¶æ‡¶ï‡¶≤‡ßá ‡¶¨‡¶æ‡¶¶ ‡¶Ø‡¶æ‡¶¨‡ßá
    } else {
      current.push(s); // ‡¶®‡¶æ ‡¶•‡¶æ‡¶ï‡¶≤‡ßá ‡¶Ø‡ßã‡¶ó ‡¶π‡¶¨‡ßá
    }

    setForm({ ...form, sizes: current.join(', ') });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    try {
      const fd = new FormData();
      // ‡¶°‡¶æ‡¶ü‡¶æ ‡¶™‡¶æ‡¶†‡¶æ‡¶®‡ßã‡¶∞ ‡¶Ü‡¶ó‡ßá ‡¶´‡¶∞‡¶Æ‡ßç‡¶Ø‡¶æ‡¶ü ‡¶†‡¶ø‡¶ï ‡¶ï‡¶∞‡¶æ
      Object.keys(form).forEach(key => {
        if (key === 'sizes' || key === 'colors') {
          const arr = form[key].split(',').map(s => s.trim()).filter(Boolean);
          arr.forEach(val => fd.append(key, val));
        } else {
          fd.append(key, form[key]);
        }
      });
      images.forEach(img => fd.append('images', img));

      if (editId) {
        await axios.put(`${API}/products/${editId}`, fd);
        toast.success('‡¶Ü‡¶™‡¶°‡ßá‡¶ü ‡¶∏‡¶´‡¶≤!');
      } else {
        await axios.post(`${API}/products`, fd);
        toast.success('‡¶∏‡¶´‡¶≤‡¶≠‡¶æ‡¶¨‡ßá ‡¶Ø‡ßã‡¶ó ‡¶π‡ßü‡ßá‡¶õ‡ßá!');
      }
      navigate('/admin/products');
    } catch (err) {
      toast.error('‡¶≠‡ßÅ‡¶≤ ‡¶π‡ßü‡ßá‡¶õ‡ßá!');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="admin-layout">
      {/* Sidebar */}
      <aside className="admin-sidebar">
        <div className="admin-brand">
          <span className="admin-brand-icon">üíç</span>
          <div className="admin-brand-name">‡¶Ö‡¶≤‡¶ï‡¶æ‡¶®‡¶®‡ßç‡¶¶‡¶æ</div>
        </div>
        <nav className="admin-nav">
          <Link to="/admin" className="admin-nav-item">üìä ‡¶°‡ßç‡¶Ø‡¶æ‡¶∂‡¶¨‡ßã‡¶∞‡ßç‡¶°</Link>
          <Link to="/admin/products" className="admin-nav-item active">üíç ‡¶™‡¶£‡ßç‡¶Ø</Link>
        </nav>
      </aside>

      {/* Main Content */}
      <main className="admin-main">
        <div className="admin-topbar">
          <h1 className="admin-page-title">{editId ? '‡¶™‡¶£‡ßç‡¶Ø ‡¶è‡¶°‡¶ø‡¶ü' : '‡¶®‡¶§‡ßÅ‡¶® ‡¶™‡¶£‡ßç‡¶Ø'}</h1>
        </div>

        <div className="admin-grid-2">
          {/* Form Left */}
          <div className="admin-card">
            <h3 className="admin-card-title">‡¶™‡¶£‡ßç‡¶Ø‡ßá‡¶∞ ‡¶§‡¶•‡ßç‡¶Ø</h3>
            <div className="form-group">
              <label>‡¶®‡¶æ‡¶Æ</label>
              <input className="admin-input" value={form.name} onChange={e=>setForm({...form, name:e.target.value})} />
            </div>
            <div className="form-group">
              <label>‡¶¶‡¶æ‡¶Æ</label>
              <input type="number" className="admin-input" value={form.price} onChange={e=>setForm({...form, price:e.target.value})} />
            </div>
          </div>

          {/* Form Right - Size Selection */}
          <div className="admin-card">
            <h3 className="admin-card-title">‡¶∏‡¶æ‡¶á‡¶ú ‡¶∏‡¶ø‡¶≤‡ßá‡¶ï‡¶∂‡¶®</h3>
            <div style={{ display: 'flex', gap: '10px', flexWrap: 'wrap' }}>
              {availableSizes.map(s => {
                const isSelected = form.sizes.includes(s);
                return (
                  <button
                    key={s}
                    type="button"
                    onClick={() => toggleSize(s)}
                    style={{
                      padding: '12px 20px',
                      borderRadius: '12px',
                      border: isSelected ? 'none' : '2px solid #ffdae9',
                      background: isSelected ? 'linear-gradient(135deg, #E63946, #ff6b9d)' : '#fff',
                      color: isSelected ? '#fff' : '#1a1a2e',
                      fontWeight: 'bold',
                      cursor: 'pointer',
                      transition: '0.3s'
                    }}
                  >
                    {s}
                  </button>
                );
              })}
            </div>
            <div style={{ marginTop: '15px', color: '#E63946', fontWeight: 'bold' }}>
              ‡¶∏‡¶ø‡¶≤‡ßá‡¶ï‡ßç‡¶ü ‡¶ï‡¶∞‡¶æ ‡¶∏‡¶æ‡¶á‡¶ú: {form.sizes || '‡¶®‡¶æ‡¶á'}
            </div>
          </div>
        </div>

        <button 
          onClick={handleSubmit} 
          className="act-btn" 
          style={{ width: '100%', padding: '15px', background: '#E63946', color: '#fff', fontSize: '16px' }}
        >
          {loading ? '‡¶≤‡ßã‡¶° ‡¶π‡¶ö‡ßç‡¶õ‡ßá...' : '‡¶∏‡ßá‡¶≠ ‡¶ï‡¶∞‡ßÅ‡¶®'}
        </button>
      </main>

      <style>{`
        .admin-input { width: 100%; padding: 10px; border: 1px solid #ddd; border-radius: 8px; margin-top: 5px; }
        .form-group { margin-bottom: 15px; }
      `}</style>
    </div>
  );
}
