import React, { useState, useEffect } from 'react';
import { useNavigate, useSearchParams, Link } from 'react-router-dom';
import axios from 'axios';
import toast from 'react-hot-toast';
import { Package, Image as ImageIcon, CheckCircle, XCircle } from 'lucide-react';

const API = process.env.REACT_APP_API_URL || 'http://localhost:5000/api';

export default function AddProduct() {
  const navigate = useNavigate();
  const [searchParams] = useSearchParams();
  const editId = searchParams.get('edit');

  const [loading, setLoading] = useState(false);
  const [images, setImages] = useState([]);
  const [form, setForm] = useState({
    name: '', description: '', price: '', originalPrice: '', category: 'set-combo',
    stock: '', material: '', sizes: '', colors: '', isFeatured: false, isAvailable: true
  });

  // ‡¶®‡¶ø‡¶∞‡ßç‡¶¶‡¶ø‡¶∑‡ßç‡¶ü ‡¶∏‡¶æ‡¶á‡¶ú ‡¶Ö‡¶™‡¶∂‡¶® (‡¶Ü‡¶™‡¶®‡¶ø ‡¶Ø‡¶æ ‡¶ö‡ßá‡ßü‡ßá‡¶õ‡¶ø‡¶≤‡ßá‡¶®)
  const availableSizes = ['2.2"', '2.4"', '2.6"', '2.8"'];

  useEffect(() => {
    if (editId) {
      axios.get(`${API}/products/${editId}`).then(r => {
        const p = r.data;
        setForm({
          name: p.name, description: p.description, price: p.price, originalPrice: p.originalPrice || '',
          category: p.category, stock: p.stock, material: p.material || '',
          sizes: p.sizes?.join(', ') || '', colors: p.colors?.join(', ') || '',
          isFeatured: p.isFeatured, isAvailable: p.isAvailable
        });
      });
    }
  }, [editId]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    try {
      const fd = new FormData();
      Object.keys(form).forEach(key => {
        if (key === 'sizes' || key === 'colors') {
          const arr = form[key].split(',').map(s => s.trim()).filter(Boolean);
          arr.forEach(val => fd.append(key, val));
        } else {
          fd.append(key, form[key]);
        }
      });
      images.forEach(img => fd.append('images', img));

      if (editId) {
        await axios.put(`${API}/products/${editId}`, fd);
        toast.success('‡¶™‡¶£‡ßç‡¶Ø ‡¶Ü‡¶™‡¶°‡ßá‡¶ü ‡¶∏‡¶´‡¶≤!');
      } else {
        await axios.post(`${API}/products`, fd);
        toast.success('‡¶®‡¶§‡ßÅ‡¶® ‡¶™‡¶£‡ßç‡¶Ø ‡¶Ø‡ßã‡¶ó ‡¶π‡ßü‡ßá‡¶õ‡ßá!');
      }
      navigate('/admin/products');
    } catch (err) {
      toast.error('‡¶≠‡ßÅ‡¶≤ ‡¶π‡ßü‡ßá‡¶õ‡ßá: ' + (err.response?.data?.message || err.message));
    } finally {
      setLoading(false);
    }
  };

  const toggleSize = (s) => {
    const current = form.sizes.split(',').map(x => x.trim()).filter(Boolean);
    const idx = current.indexOf(s);
    if (idx > -1) current.splice(idx, 1);
    else current.push(s);
    setForm({ ...form, sizes: current.join(', ') });
  };

  return (
    <div className="admin-layout">
      {/* Sidebar - ‡¶Ü‡¶™‡¶®‡¶æ‡¶∞ CSS ‡¶Ö‡¶®‡ßÅ‡¶Ø‡¶æ‡ßü‡ßÄ */}
      <aside className="admin-sidebar">
        <div className="admin-brand">
          <div className="admin-brand-icon">üíç</div>
          <div>
            <div className="admin-brand-name">‡¶Ö‡¶≤‡¶ï‡¶æ‡¶®‡¶®‡ßç‡¶¶‡¶æ</div>
            <div className="admin-brand-sub">ADMIN PANEL</div>
          </div>
        </div>
        <nav className="admin-nav">
          <Link to="/admin" className="admin-nav-item">üìä ‡¶°‡ßç‡¶Ø‡¶æ‡¶∂‡¶¨‡ßã‡¶∞‡ßç‡¶°</Link>
          <Link to="/admin/products" className="admin-nav-item active">üíç ‡¶™‡¶£‡ßç‡¶Ø ‡¶Æ‡ßç‡¶Ø‡¶æ‡¶®‡ßá‡¶ú‡¶Æ‡ßá‡¶®‡ßç‡¶ü</Link>
          <Link to="/admin/orders" className="admin-nav-item">üì¶ ‡¶Ö‡¶∞‡ßç‡¶°‡¶æ‡¶∞ ‡¶≤‡¶ø‡¶∏‡ßç‡¶ü</Link>
          <Link to="/admin/customers" className="admin-nav-item">üë• ‡¶ï‡¶æ‡¶∏‡ßç‡¶ü‡¶Æ‡¶æ‡¶∞</Link>
        </nav>
      </aside>

      {/* Main Content */}
      <main className="admin-main">
        <header className="admin-topbar">
          <h1 className="admin-page-title">
            {editId ? '‚úèÔ∏è ‡¶™‡¶£‡ßç‡¶Ø ‡¶∏‡¶Æ‡ßç‡¶™‡¶æ‡¶¶‡¶®‡¶æ' : '‚ûï ‡¶®‡¶§‡ßÅ‡¶® ‡¶™‡¶£‡ßç‡¶Ø ‡¶Ø‡ßã‡¶ó ‡¶ï‡¶∞‡ßÅ‡¶®'}
          </h1>
          <div className="admin-user">
            <div className="admin-avatar">A</div>
            <span>Admin</span>
          </div>
        </header>

        <form onSubmit={handleSubmit} className="admin-form-container">
          <div className="admin-grid-2">

            {/* Left Card: Basic Info */}
            <div className="admin-card">
              <h3 className="admin-card-title">‡¶Æ‡ßÇ‡¶≤ ‡¶§‡¶•‡ßç‡¶Ø</h3>
              <div className="form-group">
                <label>‡¶™‡¶£‡ßç‡¶Ø‡ßá‡¶∞ ‡¶®‡¶æ‡¶Æ *</label>
                <input 
                  type="text" className="admin-input" required
                  value={form.name} onChange={e => setForm({...form, name: e.target.value})}
                  placeholder="‡¶Ø‡ßá‡¶Æ‡¶®: ‡¶ï‡ßÅ‡¶®‡ßç‡¶¶‡¶® ‡¶¨‡¶æ‡¶≤‡¶æ ‡¶∏‡ßá‡¶ü"
                />
              </div>
              <div className="form-group">
                <label>‡¶¨‡¶ø‡¶∏‡ßç‡¶§‡¶æ‡¶∞‡¶ø‡¶§ ‡¶¨‡¶ø‡¶¨‡¶∞‡¶£ *</label>
                <textarea 
                  className="admin-input" rows="5" required
                  value={form.description} onChange={e => setForm({...form, description: e.target.value})}
                ></textarea>
              </div>
              <div className="form-grid-2">
                <div className="form-group">
                  <label>‡¶ï‡ßç‡¶Ø‡¶æ‡¶ü‡¶æ‡¶ó‡¶∞‡¶ø</label>
                  <select 
                    className="admin-input"
                    value={form.category} onChange={e => setForm({...form, category: e.target.value})}
                  >
                    <option value="set-combo">‡¶∏‡ßá‡¶ü/‡¶ï‡¶Æ‡ßç‡¶¨‡ßã</option>
                    <option value="metal-dhatu">‡¶Æ‡ßá‡¶ü‡¶æ‡¶≤/‡¶ß‡¶æ‡¶§‡ßÅ</option>
                    <option value="shuta-kapor">‡¶∏‡ßÅ‡¶§‡¶æ/‡¶ï‡¶æ‡¶™‡ßú</option>
                  </select>
                </div>
                <div className="form-group">
                  <label>‡¶â‡¶™‡¶ï‡¶∞‡¶£ (Material)</label>
                  <input 
                    type="text" className="admin-input"
                    value={form.material} onChange={e => setForm({...form, material: e.target.value})}
                    placeholder="‡¶Ø‡ßá‡¶Æ‡¶®: ‡¶Æ‡ßá‡¶ü‡¶æ‡¶≤, ‡¶∏‡ßÅ‡¶§‡¶æ"
                  />
                </div>
              </div>
            </div>

            {/* Right Card: Inventory & Price */}
            <div className="admin-card">
              <h3 className="admin-card-title">‡¶¶‡¶æ‡¶Æ‡ßá‡¶∞ ‡¶π‡¶ø‡¶∏‡¶æ‡¶¨ ‡¶ì ‡¶∏‡ßç‡¶ü‡¶ï</h3>
              <div className="form-grid-2">
                <div className="form-group">
                  <label>‡¶¨‡¶∞‡ßç‡¶§‡¶Æ‡¶æ‡¶® ‡¶¶‡¶æ‡¶Æ (‡ß≥) *</label>
                  <input 
                    type="number" className="admin-input" required
                    value={form.price} onChange={e => setForm({...form, price: e.target.value})}
                  />
                </div>
                <div className="form-group">
                  <label>‡¶Ü‡¶ó‡ßá‡¶∞ ‡¶¶‡¶æ‡¶Æ (‡ß≥)</label>
                  <input 
                    type="number" className="admin-input"
                    value={form.originalPrice} onChange={e => setForm({...form, originalPrice: e.target.value})}
                  />
                </div>
              </div>

              <div className="form-group">
                <label>‡¶∏‡ßç‡¶ü‡¶ï ‡¶™‡¶∞‡¶ø‡¶Æ‡¶æ‡¶£</label>
                <input 
                  type="number" className="admin-input"
                  value={form.stock} onChange={e => setForm({...form, stock: e.target.value})}
                />
              </div>

              <div className="form-group">
                <label>‡¶∏‡¶æ‡¶á‡¶ú ‡¶®‡¶ø‡¶∞‡ßç‡¶¨‡¶æ‡¶ö‡¶® ‡¶ï‡¶∞‡ßÅ‡¶®</label>
                <div style={{ display: 'flex', gap: '8px', flexWrap: 'wrap', marginTop: '8px' }}>
                  {availableSizes.map(s => {
                    const isSel = form.sizes.includes(s);
                    return (
                      <button
                        key={s} type="button" onClick={() => toggleSize(s)}
                        className={`act-btn ${isSel ? 'active-size' : ''}`}
                        style={{
                          padding: '8px 15px', borderRadius: '8px', cursor: 'pointer',
                          background: isSel ? 'linear-gradient(135deg, #E63946, #ff6b9d)' : '#f0f4f8',
                          color: isSel ? '#fff' : '#444', border: '1px solid #ddd', fontWeight: 'bold'
                        }}
                      >{s}</button>
                    );
                  })}
                </div>
              </div>
            </div>
          </div>

          {/* Bottom Section: Media & Status */}
          <div className="admin-card">
            <h3 className="admin-card-title">‡¶Æ‡¶ø‡¶°‡¶ø‡ßü‡¶æ ‡¶ì ‡¶∏‡ßç‡¶ü‡ßç‡¶Ø‡¶æ‡¶ü‡¶æ‡¶∏</h3>
            <div className="form-group">
              <label>‡¶™‡¶£‡ßç‡¶Ø‡¶∞ ‡¶õ‡¶¨‡¶ø ‡¶Ü‡¶™‡¶≤‡ßã‡¶°</label>
              <div className="img-upload-area" onClick={() => document.getElementById('fileInput').click()}>
                <ImageIcon className="upload-icon" />
                <p>‡¶õ‡¶¨‡¶ø‡¶ó‡ßÅ‡¶≤‡ßã ‡¶è‡¶ñ‡¶æ‡¶®‡ßá ‡¶°‡ßç‡¶∞‡ßç‡¶Ø‡¶æ‡¶ó ‡¶ï‡¶∞‡ßÅ‡¶® ‡¶Ö‡¶•‡¶¨‡¶æ ‡¶ï‡ßç‡¶≤‡¶ø‡¶ï ‡¶ï‡¶∞‡ßá ‡¶∏‡¶ø‡¶≤‡ßá‡¶ï‡ßç‡¶ü ‡¶ï‡¶∞‡ßÅ‡¶®</p>
                <input 
                  id="fileInput" type="file" hidden multiple accept="image/*"
                  onChange={e => setImages([...e.target.files])}
                />
              </div>
              {images.length > 0 && <p className="sc-label" style={{color: 'green', marginTop: '10px'}}>‚úì {images.length}‡¶ü‡¶ø ‡¶õ‡¶¨‡¶ø ‡¶™‡¶æ‡¶ì‡ßü‡¶æ ‡¶ó‡ßá‡¶õ‡ßá</p>}
            </div>

            <div style={{ display: 'flex', gap: '30px', marginTop: '20px' }}>
              <label style={{ display: 'flex', alignItems: 'center', gap: '8px', cursor: 'pointer' }}>
                <input type="checkbox" checked={form.isFeatured} onChange={e => setForm({...form, isFeatured: e.target.checked})} />
                <span>‡¶¨‡¶ø‡¶∂‡ßá‡¶∑ ‡¶™‡¶£‡ßç‡¶Ø (Featured)</span>
              </label>
              <label style={{ display: 'flex', alignItems: 'center', gap: '8px', cursor: 'pointer' }}>
                <input type="checkbox" checked={form.isAvailable} onChange={e => setForm({...form, isAvailable: e.target.checked})} />
                <span>‡¶™‡¶£‡ßç‡¶Ø‡¶ü‡¶ø ‡¶¨‡¶ø‡¶ï‡ßç‡¶∞‡ßü‡ßá‡¶∞ ‡¶ú‡¶®‡ßç‡¶Ø ‡¶è‡¶≠‡ßá‡¶á‡¶≤‡¶Ö‡ßç‡¶Ø‡¶æ‡¶¨‡¶≤</span>
              </label>
            </div>
          </div>

          <div style={{ display: 'flex', justifyContent: 'flex-end', gap: '15px', marginBottom: '40px' }}>
            <button type="button" onClick={() => navigate('/admin/products')} className="act-btn" style={{padding: '12px 25px'}}>‡¶¨‡¶æ‡¶§‡¶ø‡¶≤</button>
            <button 
              type="submit" disabled={loading} 
              className="act-btn act-edit" 
              style={{padding: '12px 40px', background: 'var(--primary)', color: 'white'}}
            >
              {loading ? '‡¶™‡ßç‡¶∞‡¶∏‡ßá‡¶∏‡¶ø‡¶Ç...' : (editId ? '‡¶Ü‡¶™‡¶°‡ßá‡¶ü ‡¶ï‡¶∞‡ßÅ‡¶®' : '‡¶™‡¶£‡ßç‡¶Ø ‡¶∏‡ßá‡¶≠ ‡¶ï‡¶∞‡ßÅ‡¶®')}
            </button>
          </div>
        </form>
      </main>

      <style>{`
        .admin-input {
          width: 100%; padding: 12px; border: 2px solid #e2e8f0; border-radius: 8px;
          margin-top: 6px; font-family: 'Hind Siliguri', sans-serif; outline: none;
        }
        .admin-input:focus { border-color: #E63946; }
        .form-group label { font-size: 14px; font-weight: 700; color: #475569; }
        .active-size { transform: scale(1.05); box-shadow: 0 4px 10px rgba(230,57,70,0.3); }
      `}</style>
    </div>
  );
}
